                updateDisplay();
            }
        }

        function buyMult() {
            if(score >= 200) {
                score -= 200;
                mult += 0.5;
                updateDisplay();
            }
        }

        document.getElementById('clickBtn').addEventListener('click', click);

        function gameLoop(time) {
            if(lastTime) {
                const delta = (time - lastTime) / 1000;
                score += auto * mult * delta;
            }
